mapper.select:
    select 
   distinct b.id
    from t_net_bar b
    
    	left join t_user_area ua on ua.districtCode=b.district_code
    
    where 1=1
    
    	and (b.netbar_name like concat('%',#{keyword},'%')
    		or b.approval_num like concat('%',#{keyword},'%')
    		or b.id like concat('%',#{keyword},'%')
    	)
    
    
    	and ua.userId=#{userId}
    
    
    	and b.city_code =#{cityCode}
    
    
	    	and b.district_code=#{districtCode}
	    
	    order by b.computer_num desc

 select 
distinct b.id,IFNULL(b.isdeployed,0) isdeployed
 from t_net_bar b
 
 	left join t_user_area ua on ua.districtCode=b.district_code
 
 where 1=1
 
 	and (b.netbar_name like concat('%',#{keyword},'%')
 		or b.approval_num like concat('%',#{keyword},'%')
 		or b.id like concat('%',#{keyword},'%')
 	)
 
 
 	and ua.userId=#{userId}
 
 
 	and b.city_code =#{cityCode}
 
 
 	and b.district_code=#{districtCode}
 
 order by b.computer_num desc
